import React, { useState } from "react";
import { BsArrowLeftCircle } from "react-icons/bs";
import { FaBars } from "react-icons/fa";
import { AiOutlineClose } from "react-icons/ai";
import { IconContext } from "react-icons";
import { Link } from "react-router-dom";
import "./CollapsibleSidebar.css";

const CollapsibleSidebar = ({ onClickSelectCategory }) => {
  const [sidebar, setSidebar] = useState(false);
  const showSidebar = () => setSidebar(!sidebar);
  return (
    <>
      <IconContext.Provider value={{ color: "white" }}>
        <div className="sidebar">
          <div className="sidebar-open-icon">
            <BsArrowLeftCircle onClick={showSidebar} />
          </div>
        </div>
        <nav className={sidebar ? "sidebar-menu active" : "sidebar-menu"}>
          <ul className="sidebar-menu-items">
            <li className="sidebar-toggle" onClick={showSidebar}>
              <span>
                <AiOutlineClose />
              </span>
            </li>
            <form>
              <label htmlFor="science-checkbox">Science</label>
              <input
                type="checkbox"
                id="science-checkbox"
                name="science-checkbox"
                onClick={() => this.onCategorySelect(0)}
                //label="Science"
                defaultChecked="true"
                className="checkbox science"
              />
            </form>
          </ul>
        </nav>
      </IconContext.Provider>
    </>
  );
};

export default CollapsibleSidebar;
